import React, { useState } from 'react';
import './index.css';
import { User, PlusCircle } from 'lucide-react';
import axios from 'axios';

function App() {
  const [user, setUser] = useState(null);
  const [username, setUsername] = useState('');

  const login = async () => {
    try {
      const response = await axios.post('http://localhost:3001/login', {
        name: username,
        email: `${username}@example.com`
      });
      setUser(response.data.user);
    } catch (error) {
      console.error('Login error:', error);
      alert('خطا در ورود. لطفاً مطمئن شوید سرور backend اجرا است.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-green-100 p-4">
      {!user ? (
        <div className="max-w-md mx-auto mt-20">
          <h1 className="text-3xl font-bold text-center mb-4">رشدمن</h1>
          <input
            type="text"
            placeholder="نام کاربری"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            onClick={login}
            className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            ورود
          </button>
        </div>
      ) : (
        <div className="max-w-md mx-auto">
          <h1 className="text-2xl font-bold mb-4">خوش آمدید، {user.name}</h1>
          <div className="bg-white p-4 rounded shadow mb-4">
            <h2 className="text-xl font-semibold">چالش‌های من</h2>
            <p className="text-gray-600">هنوز چالشی ندارید!</p>
            <button className="mt-2 flex items-center text-blue-500">
              <PlusCircle className="ml-2" /> ایجاد چالش جدید
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
